import React, { useState } from "react";
import {
  View,
  Text,
  Image,
  TextInput,
  TouchableOpacity,
  ScrollView,
  StyleSheet,
} from "react-native";
import { COLORS } from "../../constants";

export const LoginScreen = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  return (
    <ScrollView style={styles.container}>
      <View style={styles.logoContainer}>
        <View style={styles.formContainer}>
          <Image
            source={require("../../assets/intro.png")} // replace with your own logo image file
            style={styles.logo}
          />
          {/* <Text style={styles.title}>Welcome to Bawarchi App</Text> */}
        </View>
        <TouchableOpacity style={styles.button}>
          <Text style={styles.buttonText}>Continue </Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: COLORS.white, // choose your own login screen background color
  },
  logoContainer: {
    flex: 1,
    // alignItems: "center",
    // justifyContent: "center",
    alignContent: "center",
    marginTop: 150,
    width: "80%",
  },
  logo: {
    flex: 1,
    width: 200,
    height: 250,
    // alignSelf: "center",
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    color: "#000000", // choose your own login screen text color
    marginTop: 10,
  },
  formContainer: {
    padding: 20,
    width: "100%",
    justifyContent: "center",
    alignContent: "center",
    alignItems: "center",
  },
  input: {
    height: 40,
    backgroundColor: "#dddddd", // choose your own input field background color
    marginBottom: 10,
    padding: 10,
    color: "#000000", // choose your own input field text color
  },
  button: {
    backgroundColor: "#ff0000", // choose your own login button background color
    padding: 15,
    alignItems: "center",
  },
  buttonText: {
    fontSize: 16,
    fontWeight: "bold",
    color: "#ffffff", // choose your own login button text color
  },
});
