import React, { useState } from "react";
import {
  View,
  Text,
  Image,
  ImageBackground,
  TouchableOpacity,
  ScrollView,
  StyleSheet,
} from "react-native";
import { COLORS } from "../../constants";

export const LoginScreen = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  return (
    <ImageBackground
      style={{
        backgroundColor: COLORS.primary,
        height: "100%",
        justifyContent: "center",
        // marginVertical: 50,
      }}
      source={{
        uri: "https://i.pinimg.com/736x/1d/a2/18/1da218e176e153b1711ce36006f0c13a.jpg",
      }}
    >
      <ScrollView style={styles.container}>
        <View style={styles.logoContainer}>
          <View style={styles.formContainer}>
            <Image
              source={require("../../assets/intro.png")} // replace with your own logo image file
              style={styles.logo}
            />
            <Text style={styles.title}>Welcome to Bawarchi App</Text>
          </View>
        </View>

        <TouchableOpacity style={styles.button}>
          <Text style={styles.buttonText}>Continue </Text>
        </TouchableOpacity>
      </ScrollView>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  container: {
    // flex: 1,
    // backgroundColor: COLORS.white, // choose your own login screen background color
    // margin: 10,
    borderRadius: 18,
  },
  logoContainer: {
    alignItems: "center",
    justifyContent: "center",
    marginTop: 100,
  },
  logo: {
    flex: 1,
    width: 300,
    height: 300,
    // alignSelf: "center",
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    color: COLORS.white, // choose your own login screen text color
    marginTop: 10,
  },
  formContainer: {
    padding: 20,
    width: "100%",
    justifyContent: "center",
    alignContent: "center",
    alignItems: "center",
  },
  input: {
    height: 40,
    backgroundColor: "#dddddd", // choose your own input field background color
    marginBottom: 10,
    padding: 10,
    color: "#000000", // choose your own input field text color
  },
  button: {
    backgroundColor: COLORS.primary, // choose your own login button background color
    padding: 15,
    margin: 5,
    alignItems: "center",
    borderRadius: 12,
    marginVertical: 100,
  },
  buttonText: {
    fontSize: 16,
    fontWeight: "bold",
    color: "#ffffff", // choose your own login button text color
  },
});
