import React from "react";
import {
  View,
  Text,
  Image,
  ImageBackground,
  TouchableOpacity,
  ScrollView,
  StyleSheet,
} from "react-native";
import { COLORS } from "../../constants";

export const LoginScreen = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  return (
    <ScrollView style={styles.container}>
      <View style={styles.logoContainer}>
        <Image
          source={require("../../assets/login")} // replace with your own logo image file
          style={styles.logo}
        />
        <Text style={styles.title}>Welcome to My App</Text>
      </View>
      <View style={styles.formContainer}>
        <TextInput
          style={styles.input}
          placeholder="Email"
          placeholderTextColor="#aaaaaa"
          onChangeText={(text) => setEmail(text)}
          value={email}
        />
        <TextInput
          style={styles.input}
          placeholder="Password"
          placeholderTextColor="#aaaaaa"
          secureTextEntry
          onChangeText={(text) => setPassword(text)}
          value={password}
        />
        <TouchableOpacity style={styles.button}>
          <Text style={styles.buttonText}>Log In</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    // flex: 1,
    // backgroundColor: COLORS.white, // choose your own login screen background color
    // margin: 10,
    borderRadius: 18,
  },
  logoContainer: {
    alignItems: "center",
    justifyContent: "center",
    marginTop: 170,
  },
  logo: {
    flex: 1,
    width: 300,
    height: 300,
    // alignSelf: "center",
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    color: COLORS.white, // choose your own login screen text color
    marginTop: 10,
  },
  formContainer: {
    padding: 20,
    width: "100%",
    justifyContent: "center",
    alignContent: "center",
    alignItems: "center",
  },
  input: {
    height: 40,
    backgroundColor: "#dddddd", // choose your own input field background color
    marginBottom: 10,
    padding: 10,
    color: "#000000", // choose your own input field text color
  },
  button: {
    backgroundColor: COLORS.primary, // choose your own login button background color
    padding: 15,
    margin: 15,
    alignItems: "center",
    borderRadius: 12,
    marginVertical: 100,
  },
  buttonText: {
    fontSize: 16,
    fontWeight: "bold",
    color: "#ffffff", // choose your own login button text color
  },
});
